t=0
x=96
y=24

litF = { x = 100, y = 84, sprNo = 352, sprS = 2,
         sprFrames = 5, sprFrame = 0, sprTic = 8 } 

frame = 0
frame2 = 0

function TIC()

	if btn(0) then y=y-1 end
	if btn(1) then y=y+1 end
	if btn(2) then x=x-1 end
	if btn(3) then x=x+1 end

	cls(13)
	spr(288 + frame,x,y,0,1,0,0,2,2)
	spr(258 + frame2,84,84,0,1,0,0,1,1)
	spr(274 + frame2,130,84,0,1,0,0,1,1)

	spr(litF.sprNo + litF.sprFrame,100,84,0,1,0,0,litF.sprS
	    ,litF.sprS)
	spr(264 + frame2,120,84,0,1,0,0,1,1)
	
	if t%10 == 0 then
		frame = frame + 2
		frame2 = frame2 + 1
		if frame > 8 then
			frame = 0
		end
		if frame2 > 5 then
			frame2 = 0
		end
	end
	if t%litF.sprTic == 0 then
	 litF.sprFrame = litF.sprFrame + litF.sprS
		if litF.sprFrame > (litF.sprFrames - 1) * litF.sprS then
			litF.sprFrame = 0
		end
	end
	t=t+1
end

